<script setup lang="ts">
import { useMotion } from '@vueuse/motion'
import type { Variant } from '@vueuse/motion'
import { clamp, progress } from 'popmotion'
import { useMove, useDrag } from '@vueuse/gesture'
import { ref, inject } from 'vue'

const main = inject('main')

const hat = ref()
const head = ref()
const arms = ref()
const chest = ref()
const body = ref()
const legs = ref()

const instances = [hat, head, arms, chest, body, legs].map((ref, index) => {
  const instance = useMotion(ref, {
    initial: {
      y: 100,
      opacity: 0,
    },
    enter: {
      y: 0,
      opacity: 1,
      transition: {
        type: 'spring',
        stiffness: 320,
        damping: 20,
        delay: 50 * index,
        onComplete: () => {
          instance.variant.value = 'levitate'
        },
      },
    },
    levitate: {
      y: 15,
      transition: {
        duration: 1500,
        repeat: Infinity,
        ease: 'easeInOut',
        repeatType: 'mirror',
      },
    },
  })

  return instance
})

const instancesApply = (variant: Variant) => {
  instances.forEach((instance, index) => {
    instance.apply(variant)
  })
}

useDrag(
  ({ movement: [x, y], dragging }) => {
    if (!dragging) {
      instancesApply({
        x: 0,
        opacity: 1,
      })

      return
    }

    instancesApply({
      x: clamp(-300, 300, x),
    })
  },
  {
    domTarget: main,
  },
)
</script>
